import React, { Component } from 'react';

import './App.css';

class App extends Component{

  constructor(){
    super();
    this.state= {
      fruits:[]
    };
  }

  componentDidMount(){
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = 'http://tropicalfruitandveg.com/api/tfvjsonapi.php?search=all';
    fetch(proxyurl + url)
    .then(response => response.json())
    .then(fruitsApi => this.setState( {fruits: fruitsApi} ))
    console.log(fruits)
  }

  render(){
  return (
    <div className="App">
    <h1>Fruits</h1>
    </div>
  );
  }
}

export default App;
